sys_script.script:
(function executeRule(current, previous /*null when async*/) {
var UserID= new GlideRecord('sys_user');
UserID.addQuery('sys_id',current.rm_cc);
UserID.query();
if(UserID.next()){
var dept = UserID.department.getDisplayValue();
gs.addInfoMessage(dept);
gs.addInfoMessage(current.rm_cc);		

}

})(current, previous);
